import React, { useState, useEffect } from 'react';
import './App.css';

const generateId = () => `id_${new Date().getTime()}`;

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [creatorName, setCreatorName] = useState('');
  const [description, setDescription] = useState('');

  // NEW: State to control the modal visibility
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Load tasks from localStorage on initial render
  useEffect(() => {
    const savedTasks = localStorage.getItem('vintage_tasks');
    if (savedTasks) {
      setTasks(JSON.parse(savedTasks));
    }
  }, []);

  // Save tasks to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('vintage_tasks', JSON.stringify(tasks));
  }, [tasks]);

  const handleAddTask = (e) => {
    e.preventDefault();
    if (!title || !creatorName) {
      alert('Please fill in a title and your name!');
      return;
    }

    const newTask = {
      id: generateId(),
      title,
      description,
      creatorName,
      isCompleted: false,
    };

    setTasks([newTask, ...tasks]);

    // Clear form fields and close the modal
    setTitle('');
    setCreatorName('');
    setDescription('');
    setIsModalOpen(false); // NEW: Close modal on submit
  };

  const handleDeleteTask = (idToDelete) => {
    setTasks(tasks.filter(task => task.id !== idToDelete));
  };

  const toggleTaskCompletion = (idToToggle) => {
    setTasks(
      tasks.map(task =>
        task.id === idToToggle ? { ...task, isCompleted: !task.isCompleted } : task
      )
    );
  };

  return (
    <div className="app-container">
      {/* --- MODAL FOR ADDING A NEW NOTICE --- */}
      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="close-button" onClick={() => setIsModalOpen(false)}>Ã—</button>
            <div className="form-card">
              <h2>New Notice</h2>
              <form onSubmit={handleAddTask}>
                <input
                  type="text"
                  placeholder="Notice Title"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  required
                />
                <input
                  type="text"
                  placeholder="Your Name"
                  value={creatorName}
                  onChange={(e) => setCreatorName(e.target.value)}
                  required
                />
                <textarea
                  placeholder="Description (optional)"
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                ></textarea>
                <button type="submit">Add to Board</button>
              </form>
            </div>
          </div>
        </div>
      )}

      <header className="app-header">
        <h1>Arctic & Iron</h1>
        {/* NEW: Button to open the modal */}
        <button className="add-notice-btn" onClick={() => setIsModalOpen(true)}>
          + Add Notice
        </button>
      </header>

      <main>
        <div className="task-list">
          {tasks.length > 0 ? (
            tasks.map(task => (
              <div
                key={task.id}
                className={`task-item ${task.isCompleted ? 'completed' : ''}`}
              >
                <div className="task-content">
                  <h3>{task.title}</h3>
                  <p className="creator-info">Posted by: <strong>{task.creatorName}</strong></p>
                  {task.description && <p>{task.description}</p>}
                </div>
                <div className="task-actions">
                  <button onClick={() => toggleTaskCompletion(task.id)}>
                    {task.isCompleted ? 'Undo' : 'Complete'}
                  </button>
                  <button className="delete-btn" onClick={() => handleDeleteTask(task.id)}>
                    Delete
                  </button>
                </div>
              </div>
            ))
          ) : (
            <p className="no-tasks-message">Board is clear.</p>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
