import React, { useState, useEffect } from 'react';
import './App.css';

// A simple function to generate a unique ID
const generateId = () => `id_${new Date().getTime()}`;

function App() {
  // --- STATE MANAGEMENT ---
  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // States for the new task form
  const [taskTitle, setTaskTitle] = useState('');
  const [assignee, setAssignee] = useState('');

  // State for editing a task
  const [editingTaskId, setEditingTaskId] = useState(null);
  const [editingText, setEditingText] = useState('');

  // State for search and filter
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all'); // 'all', 'active', 'completed'

  // --- DATA PERSISTENCE (using localStorage) ---
  useEffect(() => {
    const savedTasks = localStorage.getItem('pro_todo_tasks_v2');
    if (savedTasks) setTasks(JSON.parse(savedTasks));
  }, []);

  useEffect(() => {
    localStorage.setItem('pro_todo_tasks_v2', JSON.stringify(tasks));
  }, [tasks]);

  // --- CRUD & CORE FUNCTIONS ---
  const handleAddTask = (e) => {
    e.preventDefault();
    if (!taskTitle.trim() || !assignee.trim()) {
      alert("Please provide both a task title and an assignee.");
      return;
    }
    const newTask = { 
      id: generateId(), 
      title: taskTitle.trim(), 
      assignee: assignee.trim(),
      isCompleted: false,
      createdAt: new Date().toISOString() // Automatically add timestamp
    };
    setTasks([newTask, ...tasks]);

    // Reset form and close modal
    setTaskTitle('');
    setAssignee('');
    setIsModalOpen(false);
  };

  const handleDeleteTask = (idToDelete) => {
    setTasks(tasks.filter(task => task.id !== idToDelete));
  };

  const toggleTaskCompletion = (idToToggle) => {
    setTasks(tasks.map(task => 
      task.id === idToToggle ? { ...task, isCompleted: !task.isCompleted } : task
    ));
  };

  const handleEdit = (task) => {
    setEditingTaskId(task.id);
    setEditingText(task.title);
  };

  const handleUpdateTask = (e) => {
    e.preventDefault();
    if (!editingText.trim()) {
        alert("Task title cannot be empty.");
        return;
    }
    setTasks(tasks.map(task => 
      task.id === editingTaskId ? { ...task, title: editingText.trim() } : task
    ));
    setEditingTaskId(null);
    setEditingText('');
  };

  // --- HELPER FUNCTION ---
  const formatTimestamp = (isoString) => {
    const date = new Date(isoString);
    return date.toLocaleString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    });
  };

  // --- FILTERING & SEARCHING LOGIC ---
  const filteredTasks = tasks
    .filter(task => {
      if (filterStatus === 'active') return !task.isCompleted;
      if (filterStatus === 'completed') return task.isCompleted;
      return true; // 'all'
    })
    .filter(task => 
      task.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      task.assignee.toLowerCase().includes(searchTerm.toLowerCase())
    );

  return (
    <div className="app-container">
      {/* --- MODAL FOR ADDING TASK --- */}
      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="close-button" onClick={() => setIsModalOpen(false)}>×</button>
            <div className="form-card">
              <h2>Add a New Todo</h2>
              <form onSubmit={handleAddTask}>
                <input
                  type="text"
                  placeholder="Task title..."
                  value={taskTitle}
                  onChange={(e) => setTaskTitle(e.target.value)}
                  required
                  autoFocus
                />
                <input
                  type="text"
                  placeholder="Assign to..."
                  value={assignee}
                  onChange={(e) => setAssignee(e.target.value)}
                  required
                />
                <button type="submit">Add Task</button>
              </form>
            </div>
          </div>
        </div>
      )}

      <header className="app-header">
        <h1>Task Manager</h1>
        <button className="add-notice-btn" onClick={() => setIsModalOpen(true)}>
          + Add New Todo
        </button>
      </header>

      <div className="controls-container">
        <input
          type="text"
          className="search-bar"
          placeholder="Search by title or assignee..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <div className="filter-buttons">
          <button onClick={() => setFilterStatus('all')} className={filterStatus === 'all' ? 'active' : ''}>All</button>
          <button onClick={() => setFilterStatus('active')} className={filterStatus === 'active' ? 'active' : ''}>Active</button>
          <button onClick={() => setFilterStatus('completed')} className={filterStatus === 'completed' ? 'active' : ''}>Completed</button>
        </div>
      </div>

      <main>
        <div className="task-list">
          {filteredTasks.length > 0 ? (
            filteredTasks.map(task => (
              <div key={task.id} className={`task-item ${task.isCompleted ? 'completed' : ''}`}>
                {editingTaskId === task.id ? (
                  <form onSubmit={handleUpdateTask} className="edit-form">
                    <input
                      type="text"
                      value={editingText}
                      onChange={(e) => setEditingText(e.target.value)}
                      autoFocus
                    />
                    <button type="submit">Save</button>
                  </form>
                ) : (
                  <>
                    <div className="task-main-content">
                        <div className="task-content" onClick={() => toggleTaskCompletion(task.id)}>
                            <h3>{task.title}</h3>
                        </div>
                        <div className="task-meta">
                            <span>Assigned to: <strong>{task.assignee}</strong></span>
                            <span>Created: {formatTimestamp(task.createdAt)}</span>
                        </div>
                    </div>
                    <div className="task-actions">
                      <button className="edit-btn" onClick={() => handleEdit(task)}>✎</button>
                      <button className="delete-btn" onClick={() => handleDeleteTask(task.id)}>×</button>
                    </div>
                  </>
                )}
              </div>
            ))
          ) : (
            <p className="no-tasks-message">No tasks match your criteria.</p>
          )}
        </div>
      </main>

      <div className="footer-info">
        <p>
          <strong>
            {tasks.filter(task => !task.isCompleted).length} tasks remaining
          </strong>
        </p>
      </div>
    </div>
  );
}

export default App;
